$(document).ready(function(){$("#slider_container").owlCarousel({items:3,nav:!0,dots:!1,loop:!0,responsive:{320:{items:1,dots:!0,nav:!1},720:{items:2,dots:!0,nav:!1},1024:{items:3,dots:!1,nav:!1}}}),$(".main__slider").each(function(){var t=$(this).find(".owl-carousel").owlCarousel();$(this).find(".js-prev").on("click",function(){t.trigger("prev.owl.carousel")}),$(this).find(".js-next").on("click",function(){t.trigger("next.owl.carousel")})}),$(".button_pink-border, .button_phone, .button_orange").click(function(){$(".popup-container").fadeIn()}),$(".button_invisible").click(function(){$(".header__menu_left").slideToggle()}),$(".popup-container").click(function(t){t.target==this&&$(this).hide()}),$(".popup-button").click(function(t){t.target==this&&$(".popup-container").hide()}),$(".header__navigation a").click(function(){var t=$(this).attr("href"),n=$(t).offset().top;return jQuery("html:not(:animated),body:not(:animated)").animate({scrollTop:n},800),!1})});